(1, 'Giới thiệu', 'Gioi thieu', '', '<p><strong>Nội dung giới thiệu An Nam Mobile</strong></p>\r\n', '', 'gioi-thieu', 1, '', '', '', '', 1, 1, '2016-09-22 07:14:21', '2017-08-16 11:08:12'),
(3, 'Chính sách bảo mật', 'Chinh sach bao mat', '', '<p>Ch&iacute;nh s&aacute;ch bảo mật th&ocirc;ng tin (Xem chi tiết tại điều 68 đến Điều 73 Nghị định 52/2013/NĐ-CP), bao gồm:</p>\r\n\r\n<h3><strong>Th&ocirc;ng tin&nbsp;thu thập</strong></h3>\r\n\r\n<p>Những loại th&ocirc;ng tin <strong>An Nam</strong> cần&nbsp;bao gồm:</p>\r\n\r\n<ul>\r\n	<li>Họ v&agrave; t&ecirc;n kh&aacute;ch h&agrave;ng, địa chỉ li&ecirc;n hệ</li>\r\n	<li>Email, số điện thoại di động, số điện thoại b&agrave;n<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<h3><strong>Phạm vi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li><strong>An Nam</strong> chỉ sử dụng th&ocirc;ng tin thu thập được cho mục đ&iacute;ch chăm s&oacute;c kh&aacute;ch h&agrave;ng</li>\r\n	<li><strong>An Nam</strong> c&oacute; thể sử dụng những th&ocirc;ng tin n&agrave;y để li&ecirc;n hệ với kh&aacute;ch h&agrave;ng bằng&nbsp;c&aacute;c h&igrave;nh thức như: thư ngỏ, đơn đặt h&agrave;ng, thư cảm ơn, th&ocirc;ng tin khuyến m&atilde;i hay dịch vụ mới<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<h3><strong>Thời gian lưu trữ</strong></h3>\r\n\r\n<ul>\r\n	<li><strong>An Nam</strong> sẽ lưu trữ th&ocirc;ng tin do kh&aacute;ch h&agrave;ng cung cấp tr&ecirc;n hệ thống phần mềm CRM trong thời gian theo d&otilde;i bảo h&agrave;nh sản phẩm.</li>\r\n	<li><strong>An Nam</strong> sẽ tiến h&agrave;nh x&oacute;a th&ocirc;ng tin kh&aacute;ch h&agrave;ng khi sản phẩm đ&atilde; qua hết thời hạn bảo h&agrave;nh hoặc kh&aacute;ch h&agrave;ng c&oacute; y&ecirc;u cầu hủy th&ocirc;ng tin đ&atilde; cung cấp.<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<h3><strong>Cam kết bảo mật&nbsp;</strong></h3>\r\n\r\n<p><strong>An Nam</strong> kh&ocirc;ng được quyền chia sẻ th&ocirc;ng tin của Qu&yacute; kh&aacute;ch cho bất kỳ một đơn vị n&agrave;o kh&aacute;c. Trong trường hợp đặc biệt, <strong>An Nam</strong> c&oacute; thể bị y&ecirc;u cầu phải tiết lộ th&ocirc;ng tin kh&aacute;ch h&agrave;ng như: khi c&oacute; căn cứ cho việc cung cấp th&ocirc;ng tin l&agrave; cần thiết để ngăn chặn c&aacute;c mối đe dọa về t&iacute;nh mạng, sức khỏe, hay cho mục đ&iacute;ch thực thi ph&aacute;p luật m&agrave; được cơ quan c&oacute; thẩm quyền y&ecirc;u cầu bằng văn bản. <strong>An Nam</strong> cam kết tu&acirc;n thủ Luật&nbsp;bảo mật&nbsp;v&agrave; c&aacute;c Nguy&ecirc;n tắc bảo mật hiện h&agrave;nh của nước Cộng h&ograve;a X&atilde; hội Chủ Nghĩa Việt Nam.</p>\r\n', '', 'chinh-sach-bao-mat', 1, '', '', '', '', 1, 1, '2016-09-22 07:14:22', '2017-08-16 11:18:11'),
(4, 'Phương thức thanh toán', 'Phuong thuc thanh toan', '', '<h3 style="text-align:justify"><strong>Thanh to&aacute;n bằng tiền mặt khi nhận h&agrave;ng, h&igrave;nh thức COD (cash on delivery)</strong></h3>\r\n\r\n<p style="text-align:justify">- &Aacute;p dụng cho tất cả kh&aacute;ch h&agrave;ng mua online qua dịch vụ chuyển ph&aacute;t nhanh, nhận h&agrave;ng, kiểm tra chất lượng, thanh to&aacute;n.</p>\r\n\r\n<h3 style="text-align:justify"><strong>Thanh to&aacute;n trả trước chuyển khoản online</strong></h3>\r\n\r\n<p style="text-align:justify">- &Aacute;p dụng cho kh&aacute;ch h&agrave;ng sử dụng phương thức thanh to&aacute;n online:</p>\r\n\r\n<ul>\r\n	<li>T&ecirc;n t&agrave;i khoản:&nbsp;<strong>CHI NH&Aacute;NH THỦ ĐỨC -</strong> <strong>C&Ocirc;NG TY CỔ PHẦN I.P.L</strong></li>\r\n	<li>Số t&agrave;i khoản:&nbsp;<strong>82901409</strong></li>\r\n	<li><strong>Ng&acirc;n h&agrave;ng Thương Mại Cổ Phần &Aacute; Ch&acirc;u (ACB) &ndash; CN THỦ ĐỨC</strong></li>\r\n</ul>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n', '', 'phuong-thuc-thanh-toan', 1, '', '', '', '', 1, 1, '2016-09-22 07:14:22', '2017-08-16 11:05:50'),
(5, 'Phí vận chuyển', 'Phi van chuyen', '', '<h3><strong>Khu vực giao h&agrave;ng</strong></h3>\r\n\r\n<p>- Phạm vi giao h&agrave;ng to&agrave;n quốc.</p>\r\n\r\n<h3><strong>Biểu ph&iacute; v&agrave; thời gian nhận h&agrave;ng</strong></h3>\r\n\r\n<p>- <strong>An Nam</strong> &aacute;p dụng thời gian v&agrave; biểu ph&iacute; giao h&agrave;ng theo biểu gi&aacute; của C&ocirc;ng ty Cổ phần Bưu Ch&iacute;nh Viettel như sau:&nbsp;<u><a href="https://www.shipchung.vn/wp-content/uploads/2016/09/BangPhidichvuvanchuyen0108-2809-2016.pdf" rel="nofollow"><span style="color:#0000ff">Biểu ph&iacute; dịch vụ vận chuyển Viettel</span></a></u><br />\r\n&nbsp;</p>\r\n', '', 'hinh-thuc-van-chuyen', 1, '', '', '', '', 1, 1, '2016-09-22 07:14:22', '2017-08-16 11:03:29'),
(6, 'Chính sách bảo hành', 'Chinh sach bao hanh', '', '<div class="clearfix">\r\n<ul>\r\n	<li>M&aacute;y c&ograve;n trong thời gian bảo h&agrave;nh hiệu lực v&agrave; c&aacute;c vấn đề hỏng h&oacute;c do lỗi nh&agrave; sản xuất.<br />\r\n	&nbsp;</li>\r\n	<li>Trong thời gian bảo h&agrave;nh hiệu lực, m&aacute;y chưa từng qua sửa chữa từ b&ecirc;n thứ ba (sửa chữa ngo&agrave;i) hoặc người d&ugrave;ng chưa tự can thiệp (tự mở m&aacute;y, can thiệp phần cứng)<br />\r\n	&nbsp;</li>\r\n	<li>Trong thời gian bảo h&agrave;nh hiệu lực, m&aacute;y điện thoại v&agrave; phụ kiện do nokia sản xuất kh&ocirc;ng bị t&aacute;c động của m&ocirc;i trường (thấm nước, h&oacute;a chất ăn m&ograve;n, bị qu&aacute; nhiệt do t&aacute;c động b&ecirc;n ngo&agrave;i)<br />\r\n	&nbsp;</li>\r\n	<li>Trong thời gian bảo h&agrave;nh hiệu lực m&aacute;y điện thoại v&agrave; phụ kiện Nokia kh&ocirc;ng bị rơi, rớt va đập mạnh dẫn tới hỏng h&oacute;c vật l&yacute;.<br />\r\n	&nbsp;</li>\r\n	<li>Nokia Care kh&ocirc;ng giải quyết c&aacute;c trường hợp trao đổi v&agrave; mua b&aacute;n m&aacute;y<br />\r\n	&nbsp;</li>\r\n</ul>\r\n</div>\r\n', '', 'chinh-sach-bao-hanh', 1, '', '', '', '', 1, 1, '2016-09-22 07:14:22', '2017-08-16 11:16:38'),
(8, 'Hướng dẫn đặt hàng', 'Huong dan dat hang', '', '<p>Nội dung đang được cập nhật.</p>\r\n', '', 'huong-dan-dat-hang', 1, '', '', '', '', 1, 1, '2017-08-16 11:20:04', '2017-08-16 11:20:04');
